contracts/interfaces/core/IAccessControl.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IAccessController
 * @notice Unified access control system for the pluggable architecture
 * @dev Combines role-based and permission-based access control
 *
 * Features:
 * - Role-based access control (RBAC)
 * - Fine-grained permissions
 * - Module-specific access control
 * - Time-locked operations
 * - Emergency pause capabilities
 */
interface IAccessController {
    /**
     * @notice Permission structure for fine-grained access control
     */
    struct Permission {
        bytes32 permissionId;       // Unique permission identifier
        string name;                // Human-readable name
        string description;         // Permission description
        bool requiresTimelock;      // Whether this permission requires timelock
        uint256 timelockDuration;   // Timelock duration in seconds
        bool isActive;              // Whether permission is currently active
    }

    /**
     * @notice Role structure
     */
    struct Role {
        bytes32 roleId;             // Unique role identifier
        string name;                // Human-readable name
        bytes32[] permissions;      // Permissions granted to this role
        bytes32 adminRole;          // Role that can grant/revoke this role
        bool isActive;              // Whether role is currently active
    }

    /**
     * @notice Time-locked operation
     */
    struct TimeLockOperation {
        bytes32 operationId;        // Unique operation identifier
        address executor;           // Address that initiated the operation
        bytes32 permission;         // Required permission
        bytes callData;             // Encoded function call
        uint256 scheduledTime;      // When operation was scheduled
        uint256 executeAfter;       // Earliest execution time
        bool executed;              // Whether operation has been executed
        bool cancelled;             // Whether operation was cancelled
    }

    // ============ Events ============

    event RoleGranted(bytes32 indexed roleId, address indexed account, address indexed sender);
    event RoleRevoked(bytes32 indexed roleId, address indexed account, address indexed sender);
    event RoleCreated(bytes32 indexed roleId, string name, bytes32 indexed adminRole);

    event PermissionGranted(bytes32 indexed permissionId, bytes32 indexed roleId, address indexed sender);
    event PermissionRevoked(bytes32 indexed permissionId, bytes32 indexed roleId, address indexed sender);
    event PermissionCreated(bytes32 indexed permissionId, string name, bool requiresTimelock);

    event OperationScheduled(bytes32 indexed operationId, address indexed executor, uint256 executeAfter);
    event OperationExecuted(bytes32 indexed operationId, address indexed executor);
    event OperationCancelled(bytes32 indexed operationId, address indexed canceller);

    event EmergencyPauseActivated(address indexed activator);
    event EmergencyPauseDeactivated(address indexed deactivator);
    event ModuleAccessRevoked(bytes32 indexed moduleId, address indexed revoker);

    // ============ Role Management ============

    /**
     * @notice Create a new role
     * @param roleId Unique identifier for the role
     * @param name Human-readable role name
     * @param adminRole Role that can grant/revoke this role
     * @dev Only callable by DEFAULT_ADMIN_ROLE
     */
    function createRole(bytes32 roleId, string calldata name, bytes32 adminRole) external;

    /**
     * @notice Grant a role to an account
     * @param roleId Role identifier
     * @param account Address to grant role to
     * @dev Caller must have the role's admin role
     */
    function grantRole(bytes32 roleId, address account) external;

    /**
     * @notice Revoke a role from an account
     * @param roleId Role identifier
     * @param account Address to revoke role from
     * @dev Caller must have the role's admin role
     */
    function revokeRole(bytes32 roleId, address account) external;

    /**
     * @notice Renounce a role (caller gives up their own role)
     * @param roleId Role identifier
     */
    function renounceRole(bytes32 roleId) external;

    /**
     * @notice Check if account has a specific role
     * @param roleId Role identifier
     * @param account Address to check
     * @return True if account has the role
     */
    function hasRole(bytes32 roleId, address account) external view returns (bool);

    /**
     * @notice Get role information
     * @param roleId Role identifier
     * @return Role structure
     */
    function getRole(bytes32 roleId) external view returns (Role memory);

    /**
     * @notice Get all roles for an account
     * @param account Address to check
     * @return Array of role IDs
     */
    function getRolesForAccount(address account) external view returns (bytes32[] memory);

    // ============ Permission Management ============

    /**
     * @notice Create a new permission
     * @param permissionId Unique identifier
     * @param name Human-readable name
     * @param description Permission description
     * @param requiresTimelock Whether timelock is required
     * @param timelockDuration Timelock duration in seconds (if applicable)
     */
    function createPermission(
        bytes32 permissionId,
        string calldata name,
        string calldata description,
        bool requiresTimelock,
        uint256 timelockDuration
    ) external;

    /**
     * @notice Grant permission to a role
     * @param permissionId Permission identifier
     * @param roleId Role identifier
     */
    function grantPermissionToRole(bytes32 permissionId, bytes32 roleId) external;

    /**
     * @notice Revoke permission from a role
     * @param permissionId Permission identifier
     * @param roleId Role identifier
     */
    function revokePermissionFromRole(bytes32 permissionId, bytes32 roleId) external;

    /**
     * @notice Check if account has a specific permission
     * @param permissionId Permission identifier
     * @param account Address to check
     * @return True if account has the permission
     */
    function hasPermission(bytes32 permissionId, address account) external view returns (bool);

    /**
     * @notice Check if account has permission for a specific module
     * @param moduleId Module identifier
     * @param permissionId Permission identifier
     * @param account Address to check
     * @return True if account has module-specific permission
     */
    function hasModulePermission(
        bytes32 moduleId,
        bytes32 permissionId,
        address account
    ) external view returns (bool);

    /**
     * @notice Get permission information
     * @param permissionId Permission identifier
     * @return Permission structure
     */
    function getPermission(bytes32 permissionId) external view returns (Permission memory);

    // ============ Time-Locked Operations ============

    /**
     * @notice Schedule a time-locked operation
     * @param permission Required permission
     * @param target Target contract address
     * @param callData Encoded function call
     * @return operationId Unique operation identifier
     */
    function scheduleOperation(
        bytes32 permission,
        address target,
        bytes calldata callData
    ) external returns (bytes32 operationId);

    /**
     * @notice Execute a scheduled operation
     * @param operationId Operation identifier
     * @dev Can only be executed after timelock expires
     */
    function executeOperation(bytes32 operationId) external returns (bytes memory);

    /**
     * @notice Cancel a scheduled operation
     * @param operationId Operation identifier
     * @dev Only callable by operation creator or admin
     */
    function cancelOperation(bytes32 operationId) external;

    /**
     * @notice Get operation details
     * @param operationId Operation identifier
     * @return Operation structure
     */
    function getOperation(bytes32 operationId) external view returns (TimeLockOperation memory);

    /**
     * @notice Check if operation is ready to execute
     * @param operationId Operation identifier
     * @return True if operation can be executed
     */
    function isOperationReady(bytes32 operationId) external view returns (bool);

    /**
     * @notice Get pending operations for an executor
     * @param executor Address to check
     * @return Array of operation IDs
     */
    function getPendingOperations(address executor) external view returns (bytes32[] memory);

    // ============ Module-Specific Access Control ============

    /**
     * @notice Grant module-specific role
     * @param moduleId Module identifier
     * @param roleId Role identifier
     * @param account Address to grant role to
     */
    function grantModuleRole(bytes32 moduleId, bytes32 roleId, address account) external;

    /**
     * @notice Revoke module-specific role
     * @param moduleId Module identifier
     * @param roleId Role identifier
     * @param account Address to revoke role from
     */
    function revokeModuleRole(bytes32 moduleId, bytes32 roleId, address account) external;

    /**
     * @notice Check if account has module-specific role
     * @param moduleId Module identifier
     * @param roleId Role identifier
     * @param account Address to check
     * @return True if account has the module role
     */
    function hasModuleRole(
        bytes32 moduleId,
        bytes32 roleId,
        address account
    ) external view returns (bool);

    // ============ Emergency Controls ============

    /**
     * @notice Activate emergency pause
     * @dev Pauses all operations across all modules
     * @dev Only callable by EMERGENCY_ROLE
     */
    function activateEmergencyPause() external;

    /**
     * @notice Deactivate emergency pause
     * @dev Only callable by DEFAULT_ADMIN_ROLE
     */
    function deactivateEmergencyPause() external;

    /**
     * @notice Check if system is in emergency pause
     * @return True if emergency pause is active
     */
    function isEmergencyPaused() external view returns (bool);

    /**
     * @notice Revoke all access for a specific module
     * @param moduleId Module identifier
     * @dev Emergency function to isolate a compromised module
     */
    function emergencyRevokeModuleAccess(bytes32 moduleId) external;

    // ============ View Functions ============

    /**
     * @notice Get default admin role identifier
     * @return Default admin role ID
     */
    function DEFAULT_ADMIN_ROLE() external pure returns (bytes32);

    /**
     * @notice Get emergency role identifier
     * @return Emergency role ID
     */
    function EMERGENCY_ROLE() external pure returns (bytes32);

    /**
     * @notice Get access control statistics
     * @return totalRoles Total number of roles
     * @return totalPermissions Total number of permissions
     * @return activeRoles Number of active roles
     */
    function getAccessControlStats()
        external
        view
        returns (
            uint256 totalRoles,
            uint256 totalPermissions,
            uint256 activeRoles
        );
}

contracts/interfaces/core/IEventHub.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IEventHub
 * @notice Centralized event bus for inter-module communication
 * @dev Enables loose coupling between modules through event-based communication
 *
 * Design Principles:
 * - Modules publish events to the hub
 * - Modules can subscribe to events from other modules
 * - Prevents direct dependencies between modules
 * - Supports event filtering and routing
 * - Maintains event history for auditing
 */
interface IEventHub {
    /**
     * @notice Event types for categorization
     */
    enum EventCategory {
        SYSTEM,         // System-level events
        MODULE,         // Module lifecycle events
        TRANSACTION,    // Transaction-related events
        GOVERNANCE,     // Governance events
        ORACLE,         // Price oracle events
        AUDIT,          // Audit events
        CUSTOM          // Custom module events
    }

    /**
     * @notice Event severity levels
     */
    enum EventSeverity {
        INFO,           // Informational
        WARNING,        // Warning
        ERROR,          // Error
        CRITICAL        // Critical
    }

    /**
     * @notice Standardized event structure
     */
    struct HubEvent {
        bytes32 eventId;            // Unique event identifier
        bytes32 moduleId;           // Source module identifier
        EventCategory category;     // Event category
        EventSeverity severity;     // Event severity
        string eventType;           // Event type name (e.g., "DEPOSIT", "WITHDRAWAL")
        bytes eventData;            // ABI-encoded event data
        uint256 timestamp;          // Event timestamp
        uint256 blockNumber;        // Block number
        address emitter;            // Address that emitted the event
    }

    /**
     * @notice Event subscription
     */
    struct EventSubscription {
        bytes32 subscriptionId;     // Unique subscription identifier
        bytes32 subscriberModule;   // Subscribing module ID
        bytes32 publisherModule;    // Publisher module ID (bytes32(0) = all)
        EventCategory category;     // Category filter (optional)
        string eventType;           // Event type filter (empty = all)
        address callbackAddress;    // Address to call on event
        bytes4 callbackSelector;    // Function selector for callback
        bool isActive;              // Subscription status
    }

    /**
     * @notice Event filter criteria
     */
    struct EventFilter {
        bytes32 moduleId;           // Filter by module (bytes32(0) = all)
        EventCategory category;     // Filter by category
        EventSeverity minSeverity;  // Minimum severity level
        uint256 fromTimestamp;      // Start timestamp
        uint256 toTimestamp;        // End timestamp
        uint256 fromBlock;          // Start block
        uint256 toBlock;            // End block
    }

    // ============ Events ============

    event EventPublished(
        bytes32 indexed eventId,
        bytes32 indexed moduleId,
        EventCategory indexed category,
        string eventType,
        uint256 timestamp
    );

    event EventSubscribed(
        bytes32 indexed subscriptionId,
        bytes32 indexed subscriberModule,
        bytes32 indexed publisherModule,
        string eventType
    );

    event EventUnsubscribed(
        bytes32 indexed subscriptionId,
        bytes32 indexed subscriberModule
    );

    event EventCallbackExecuted(
        bytes32 indexed eventId,
        bytes32 indexed subscriptionId,
        bool success
    );

    event EventCallbackFailed(
        bytes32 indexed eventId,
        bytes32 indexed subscriptionId,
        string reason
    );

    event EventRouted(
        bytes32 indexed eventId,
        bytes32 indexed fromModule,
        bytes32 indexed toModule
    );

    // ============ Event Publishing ============

    /**
     * @notice Publish an event to the hub
     * @param category Event category
     * @param severity Event severity
     * @param eventType Event type name
     * @param eventData ABI-encoded event data
     * @return eventId Unique event identifier
     * @dev Only callable by registered modules
     */
    function publishEvent(
        EventCategory category,
        EventSeverity severity,
        string calldata eventType,
        bytes calldata eventData
    ) external returns (bytes32 eventId);

    /**
     * @notice Publish a batch of events
     * @param categories Array of event categories
     * @param severities Array of event severities
     * @param eventTypes Array of event type names
     * @param eventDataArray Array of ABI-encoded event data
     * @return eventIds Array of event identifiers
     * @dev Gas-optimized batch operation
     */
    function publishEventBatch(
        EventCategory[] calldata categories,
        EventSeverity[] calldata severities,
        string[] calldata eventTypes,
        bytes[] calldata eventDataArray
    ) external returns (bytes32[] memory eventIds);

    /**
     * @notice Emit a pre-defined system event
     * @param eventType System event type
     * @param eventData Event data
     * @return eventId Event identifier
     * @dev Convenience function for common system events
     */
    function emitSystemEvent(
        string calldata eventType,
        bytes calldata eventData
    ) external returns (bytes32 eventId);

    // ============ Event Subscription ============

    /**
     * @notice Subscribe to events from a specific module
     * @param publisherModule Module to subscribe to (bytes32(0) for all modules)
     * @param category Event category filter
     * @param eventType Event type filter (empty string for all types)
     * @param callbackAddress Address to call when event occurs
     * @param callbackSelector Function selector for callback
     * @return subscriptionId Unique subscription identifier
     * @dev Callback function should have signature: callback(bytes32 eventId, bytes calldata data)
     */
    function subscribe(
        bytes32 publisherModule,
        EventCategory category,
        string calldata eventType,
        address callbackAddress,
        bytes4 callbackSelector
    ) external returns (bytes32 subscriptionId);

    /**
     * @notice Unsubscribe from events
     * @param subscriptionId Subscription identifier
     */
    function unsubscribe(bytes32 subscriptionId) external;

    /**
     * @notice Update subscription status
     * @param subscriptionId Subscription identifier
     * @param isActive New status
     */
    function setSubscriptionActive(bytes32 subscriptionId, bool isActive) external;

    /**
     * @notice Get subscription details
     * @param subscriptionId Subscription identifier
     * @return Subscription structure
     */
    function getSubscription(bytes32 subscriptionId) external view returns (EventSubscription memory);

    /**
     * @notice Get all subscriptions for a module
     * @param moduleId Module identifier
     * @return Array of subscription IDs
     */
    function getModuleSubscriptions(bytes32 moduleId) external view returns (bytes32[] memory);

    // ============ Event Querying ============

    /**
     * @notice Get event by ID
     * @param eventId Event identifier
     * @return Event structure
     */
    function getEvent(bytes32 eventId) external view returns (HubEvent memory);

    /**
     * @notice Get events by filter
     * @param filter Event filter criteria
     * @param offset Starting index
     * @param limit Maximum number of events to return
     * @return events Array of matching events
     */
    function getEventsByFilter(
        EventFilter calldata filter,
        uint256 offset,
        uint256 limit
    ) external view returns (HubEvent[] memory events);

    /**
     * @notice Get recent events
     * @param count Number of recent events to return
     * @return events Array of recent events
     */
    function getRecentEvents(uint256 count) external view returns (HubEvent[] memory events);

    /**
     * @notice Get events for a specific module
     * @param moduleId Module identifier
     * @param offset Starting index
     * @param limit Maximum number of events to return
     * @return events Array of module events
     */
    function getModuleEvents(
        bytes32 moduleId,
        uint256 offset,
        uint256 limit
    ) external view returns (HubEvent[] memory events);

    /**
     * @notice Get events by category
     * @param category Event category
     * @param offset Starting index
     * @param limit Maximum number of events to return
     * @return events Array of events in category
     */
    function getEventsByCategory(
        EventCategory category,
        uint256 offset,
        uint256 limit
    ) external view returns (HubEvent[] memory events);

    /**
     * @notice Get events by severity
     * @param minSeverity Minimum severity level
     * @param offset Starting index
     * @param limit Maximum number of events to return
     * @return events Array of events meeting severity criteria
     */
    function getEventsBySeverity(
        EventSeverity minSeverity,
        uint256 offset,
        uint256 limit
    ) external view returns (HubEvent[] memory events);

    /**
     * @notice Get event count for a module
     * @param moduleId Module identifier
     * @return Total number of events from module
     */
    function getModuleEventCount(bytes32 moduleId) external view returns (uint256);

    // ============ Event Routing ============

    /**
     * @notice Route event to specific module
     * @param eventId Event identifier
     * @param targetModule Target module identifier
     * @dev Allows manual event routing for special cases
     */
    function routeEvent(bytes32 eventId, bytes32 targetModule) external;

    /**
     * @notice Set up automatic event routing rule
     * @param sourceModule Source module identifier
     * @param targetModule Target module identifier
     * @param eventType Event type to route (empty = all)
     * @return ruleId Routing rule identifier
     */
    function addRoutingRule(
        bytes32 sourceModule,
        bytes32 targetModule,
        string calldata eventType
    ) external returns (bytes32 ruleId);

    /**
     * @notice Remove routing rule
     * @param ruleId Routing rule identifier
     */
    function removeRoutingRule(bytes32 ruleId) external;

    // ============ Statistics & Monitoring ============

    /**
     * @notice Get event hub statistics
     * @return totalEvents Total events published
     * @return totalSubscriptions Total active subscriptions
     * @return eventsLast24h Events in last 24 hours
     * @return criticalEventsLast24h Critical events in last 24 hours
     */
    function getEventHubStats()
        external
        view
        returns (
            uint256 totalEvents,
            uint256 totalSubscriptions,
            uint256 eventsLast24h,
            uint256 criticalEventsLast24h
        );

    /**
     * @notice Get module activity statistics
     * @param moduleId Module identifier
     * @return eventCount Total events from module
     * @return lastEventTime Timestamp of last event
     * @return subscriptionCount Number of subscriptions
     */
    function getModuleActivityStats(bytes32 moduleId)
        external
        view
        returns (
            uint256 eventCount,
            uint256 lastEventTime,
            uint256 subscriptionCount
        );

    // ============ Configuration ============

    /**
     * @notice Set maximum events to store
     * @param maxEvents Maximum number of events
     * @dev Older events are pruned when limit is reached
     */
    function setMaxStoredEvents(uint256 maxEvents) external;

    /**
     * @notice Enable/disable event callbacks
     * @param enabled Whether callbacks are enabled
     * @dev Can disable in case of emergency
     */
    function setCallbacksEnabled(bool enabled) external;

    /**
     * @notice Check if callbacks are enabled
     * @return True if callbacks are enabled
     */
    function areCallbacksEnabled() external view returns (bool);
}

contracts/interfaces/IUpgradeable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IUpgradeable
 * @notice Interface for upgradeable modules using UUPS pattern
 * @dev Extends ERC1967 UUPS standard with additional safety features
 */
interface IUpgradeable {
    /**
     * @notice Emitted when implementation is upgraded
     * @param oldImplementation Previous implementation address
     * @param newImplementation New implementation address
     * @param version New version string
     */
    event Upgraded(
        address indexed oldImplementation,
        address indexed newImplementation,
        string version
    );

    /**
     * @notice Emitted when upgrade is authorized
     * @param implementation Implementation address
     * @param authorizer Address that authorized the upgrade
     */
    event UpgradeAuthorized(address indexed implementation, address indexed authorizer);

    /**
     * @notice Emitted when upgrade authorization is revoked
     * @param implementation Implementation address
     */
    event UpgradeAuthorizationRevoked(address indexed implementation);

    /**
     * @notice Upgrade to a new implementation
     * @param newImplementation Address of the new implementation
     * @dev Can only be called by authorized upgraders
     */
    function upgradeTo(address newImplementation) external;

    /**
     * @notice Upgrade to a new implementation and call a function
     * @param newImplementation Address of the new implementation
     * @param data Function call data
     * @dev Can only be called by authorized upgraders
     */
    function upgradeToAndCall(address newImplementation, bytes memory data) external payable;

    /**
     * @notice Get the current implementation address
     * @return Implementation contract address
     */
    function getImplementation() external view returns (address);

    /**
     * @notice Get the current implementation version
     * @return Version string
     */
    function getImplementationVersion() external view returns (string memory);

    /**
     * @notice Check if an address is authorized to upgrade
     * @param account Address to check
     * @return True if authorized
     */
    function canUpgrade(address account) external view returns (bool);

    /**
     * @notice Authorize an address to perform upgrades
     * @param account Address to authorize
     * @dev Only callable by admin
     */
    function authorizeUpgrader(address account) external;

    /**
     * @notice Revoke upgrade authorization
     * @param account Address to revoke
     * @dev Only callable by admin
     */
    function revokeUpgradeAuthorization(address account) external;

    /**
     * @notice Validate a new implementation before upgrade
     * @param newImplementation Address to validate
     * @return valid True if implementation is valid
     * @return reason Reason if invalid
     */
    function validateUpgrade(address newImplementation)
        external
        view
        returns (bool valid, string memory reason);

    /**
     * @notice Perform pre-upgrade checks and preparation
     * @param newImplementation Address of new implementation
     * @dev Called before upgrade, can revert to prevent upgrade
     */
    function beforeUpgrade(address newImplementation) external;

    /**
     * @notice Perform post-upgrade initialization
     * @param oldImplementation Address of previous implementation
     * @dev Called after upgrade completes
     */
    function afterUpgrade(address oldImplementation) external;

    /**
     * @notice Get upgrade history
     * @return implementations Array of implementation addresses
     * @return timestamps Array of upgrade timestamps
     * @return versions Array of version strings
     */
    function getUpgradeHistory()
        external
        view
        returns (
            address[] memory implementations,
            uint256[] memory timestamps,
            string[] memory versions
        );

    /**
     * @notice Emergency pause upgrades
     * @dev Can only be called by admin
     */
    function pauseUpgrades() external;

    /**
     * @notice Resume upgrades
     * @dev Can only be called by admin
     */
    function resumeUpgrades() external;

    /**
     * @notice Check if upgrades are paused
     * @return True if paused
     */
    function upgradesPaused() external view returns (bool);
}

contracts/layer1/bridges/L1Gateway.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../core/CollateralVaultL1.sol";
import "../core/LoyaltyUSDL1.sol";

/**
 * @title L1Gateway
 * @notice Layer 1 bridge gateway for cross-chain operations
 * @dev Integrates with Arbitrum's native bridge for L1<->L2 communication
 *
 * Architecture:
 * - Uses Arbitrum's native Inbox/Outbox for message passing
 * - Handles collateral deposits to L2
 * - Processes L2 state updates
 * - Manages emergency exits
 *
 * Integration with Arbitrum:
 * - Inbox: 0x4Dbd4fc535Ac27206064B68FfCf827b0A60BAB3f (Mainnet)
 * - Uses retryable tickets for guaranteed L2 execution
 */

// Arbitrum Inbox interface
interface IInbox {
    function createRetryableTicket(
        address to,
        uint256 l2CallValue,
        uint256 maxSubmissionCost,
        address excessFeeRefundAddress,
        address callValueRefundAddress,
        uint256 gasLimit,
        uint256 maxFeePerGas,
        bytes calldata data
    ) external payable returns (uint256);
}

// Arbitrum Outbox interface
interface IOutbox {
    function l2ToL1Sender() external view returns (address);
}

contract L1Gateway is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // ============ State Variables ============

    // Core L1 contracts
    CollateralVaultL1 public immutable collateralVault;
    LoyaltyUSDL1 public immutable loyaltyUSD;
    IERC20 public immutable collateralToken;

    // Arbitrum bridge contracts
    IInbox public inbox;
    IOutbox public outbox;

    // L2 counterpart address
    address public l2Gateway;

    // Gas parameters for L2 calls
    uint256 public maxSubmissionCost = 0.01 ether;
    uint256 public maxGas = 300000;
    uint256 public gasPriceBid = 1 gwei;

    // Deposit tracking
    struct Deposit {
        address user;
        uint256 amount;
        uint256 timestamp;
        bytes32 l2TxHash;
        bool processed;
    }

    mapping(uint256 => Deposit) public deposits;
    uint256 public depositNonce;

    // Withdrawal tracking
    struct Withdrawal {
        address user;
        uint256 amount;
        uint256 timestamp;
        bool executed;
    }

    mapping(bytes32 => Withdrawal) public withdrawals;

    // Emergency pause
    bool public paused;

    // ============ Events ============

    event DepositInitiated(
        uint256 indexed depositId,
        address indexed user,
        uint256 amount,
        uint256 indexed ticketId
    );

    event DepositFinalized(
        uint256 indexed depositId,
        address indexed user,
        uint256 amount
    );

    event WithdrawalInitiated(
        bytes32 indexed withdrawalId,
        address indexed user,
        uint256 amount
    );

    event WithdrawalExecuted(
        bytes32 indexed withdrawalId,
        address indexed user,
        uint256 amount
    );

    event L2GatewayUpdated(address indexed oldGateway, address indexed newGateway);
    event GasParametersUpdated(uint256 maxSubmissionCost, uint256 maxGas, uint256 gasPriceBid);

    // ============ Modifiers ============

    modifier whenNotPaused() {
        require(!paused, "L1Gateway: paused");
        _;
    }

    modifier onlyL2() {
        require(msg.sender == address(outbox), "L1Gateway: not from outbox");
        require(outbox.l2ToL1Sender() == l2Gateway, "L1Gateway: not from L2 gateway");
        _;
    }

    // ============ Constructor ============

    constructor(
        address _collateralVault,
        address _loyaltyUSD,
        address _collateralToken,
        address _inbox,
        address _outbox
    ) Ownable(msg.sender) {
        require(_collateralVault != address(0), "Invalid vault");
        require(_loyaltyUSD != address(0), "Invalid LUSD");
        require(_collateralToken != address(0), "Invalid collateral");
        require(_inbox != address(0), "Invalid inbox");
        require(_outbox != address(0), "Invalid outbox");

        collateralVault = CollateralVaultL1(_collateralVault);
        loyaltyUSD = LoyaltyUSDL1(_loyaltyUSD);
        collateralToken = IERC20(_collateralToken);
        inbox = IInbox(_inbox);
        outbox = IOutbox(_outbox);
    }

    // ============ Deposit Functions ============

    /**
     * @notice Deposit collateral to L2
     * @param amount Amount of collateral to deposit
     */
    function depositToL2(uint256 amount)
        external
        payable
        whenNotPaused
        nonReentrant
        returns (uint256 depositId, uint256 ticketId)
    {
        require(amount > 0, "Amount must be > 0");
        require(l2Gateway != address(0), "L2 gateway not set");
        require(msg.value >= maxSubmissionCost, "Insufficient ETH for submission");

        // Increment nonce
        depositId = depositNonce++;

        // Transfer collateral from user to this contract
        collateralToken.safeTransferFrom(msg.sender, address(this), amount);

        // Approve vault to spend (using forceApprove for OZ 5.x)
        collateralToken.forceApprove(address(collateralVault), amount);

        // Lock in vault
        bytes32 l2TxHash = keccak256(abi.encodePacked(depositId, msg.sender, amount, block.timestamp));
        collateralVault.lockCollateral(msg.sender, amount, l2TxHash);

        // Create retryable ticket to L2
        bytes memory data = abi.encodeWithSignature(
            "finalizeDeposit(uint256,address,uint256)",
            depositId,
            msg.sender,
            amount
        );

        ticketId = inbox.createRetryableTicket{value: msg.value}(
            l2Gateway,
            0, // l2CallValue
            maxSubmissionCost,
            msg.sender, // excessFeeRefundAddress
            msg.sender, // callValueRefundAddress
            maxGas,
            gasPriceBid,
            data
        );

        // Store deposit info
        deposits[depositId] = Deposit({
            user: msg.sender,
            amount: amount,
            timestamp: block.timestamp,
            l2TxHash: l2TxHash,
            processed: false
        });

        emit DepositInitiated(depositId, msg.sender, amount, ticketId);
    }

    // ============ Withdrawal Functions ============

    /**
     * @notice Finalize withdrawal from L2
     * @param withdrawalId Withdrawal identifier
     * @param user User address
     * @param amount Amount to withdraw
     * @dev Called by L2 via Arbitrum's outbox
     */
    function finalizeWithdrawal(
        bytes32 withdrawalId,
        address user,
        uint256 amount
    ) external onlyL2 nonReentrant {
        require(user != address(0), "Invalid user");
        require(amount > 0, "Invalid amount");
        require(!withdrawals[withdrawalId].executed, "Already executed");

        // Record withdrawal
        withdrawals[withdrawalId] = Withdrawal({
            user: user,
            amount: amount,
            timestamp: block.timestamp,
            executed: true
        });

        // Unlock from vault
        bytes32 l2TxHash = withdrawalId;
        collateralVault.unlockCollateral(user, amount, l2TxHash);

        emit WithdrawalExecuted(withdrawalId, user, amount);
    }

    // ============ LUSD Minting/Burning (Bridge Operations) ============

    /**
     * @notice Mint LUSD based on L2 state
     * @param user User address
     * @param amount Amount to mint
     * @param l2TxHash L2 transaction hash
     * @dev Called by L2 via Arbitrum's outbox
     */
    function bridgeMint(address user, uint256 amount, bytes32 l2TxHash)
        external
        onlyL2
        nonReentrant
    {
        require(user != address(0), "Invalid user");
        require(amount > 0, "Invalid amount");

        loyaltyUSD.bridgeMint(user, amount, l2TxHash);
    }

    /**
     * @notice Burn LUSD based on L2 state
     * @param user User address
     * @param amount Amount to burn
     * @param l2TxHash L2 transaction hash
     * @dev Called by L2 via Arbitrum's outbox
     */
    function bridgeBurn(address user, uint256 amount, bytes32 l2TxHash)
        external
        onlyL2
        nonReentrant
    {
        require(user != address(0), "Invalid user");
        require(amount > 0, "Invalid amount");

        loyaltyUSD.bridgeBurn(user, amount, l2TxHash);
    }

    // ============ Admin Functions ============

    /**
     * @notice Set L2 gateway address
     * @param _l2Gateway L2 gateway address
     */
    function setL2Gateway(address _l2Gateway) external onlyOwner {
        require(_l2Gateway != address(0), "Invalid gateway");
        address oldGateway = l2Gateway;
        l2Gateway = _l2Gateway;
        emit L2GatewayUpdated(oldGateway, _l2Gateway);
    }

    /**
     * @notice Update gas parameters for L2 calls
     */
    function setGasParameters(
        uint256 _maxSubmissionCost,
        uint256 _maxGas,
        uint256 _gasPriceBid
    ) external onlyOwner {
        maxSubmissionCost = _maxSubmissionCost;
        maxGas = _maxGas;
        gasPriceBid = _gasPriceBid;
        emit GasParametersUpdated(_maxSubmissionCost, _maxGas, _gasPriceBid);
    }

    /**
     * @notice Update Arbitrum Inbox address
     */
    function setInbox(address _inbox) external onlyOwner {
        require(_inbox != address(0), "Invalid inbox");
        inbox = IInbox(_inbox);
    }

    /**
     * @notice Update Arbitrum Outbox address
     */
    function setOutbox(address _outbox) external onlyOwner {
        require(_outbox != address(0), "Invalid outbox");
        outbox = IOutbox(_outbox);
    }

    /**
     * @notice Pause deposits
     */
    function pause() external onlyOwner {
        paused = true;
    }

    /**
     * @notice Resume deposits
     */
    function unpause() external onlyOwner {
        paused = false;
    }

    // ============ View Functions ============

    /**
     * @notice Get deposit info
     */
    function getDeposit(uint256 depositId)
        external
        view
        returns (
            address user,
            uint256 amount,
            uint256 timestamp,
            bytes32 l2TxHash,
            bool processed
        )
    {
        Deposit memory deposit = deposits[depositId];
        return (
            deposit.user,
            deposit.amount,
            deposit.timestamp,
            deposit.l2TxHash,
            deposit.processed
        );
    }

    /**
     * @notice Get withdrawal info
     */
    function getWithdrawal(bytes32 withdrawalId)
        external
        view
        returns (address user, uint256 amount, uint256 timestamp, bool executed)
    {
        Withdrawal memory withdrawal = withdrawals[withdrawalId];
        return (
            withdrawal.user,
            withdrawal.amount,
            withdrawal.timestamp,
            withdrawal.executed
        );
    }

    /**
     * @notice Calculate required ETH for deposit
     */
    function calculateRequiredEth() external view returns (uint256) {
        return maxSubmissionCost + (maxGas * gasPriceBid);
    }
}

contracts/layer1/core/CollateralVaultL1..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title CollateralVaultL1
 * @notice Layer 1 optimized version - Only asset custody
 * @dev Simplified vault for L1, computation moved to L2
 *
 * L1 Responsibilities:
 * - Lock/unlock collateral tokens
 * - Final custody of assets
 * - Emergency withdrawals
 * - Bridge integration
 *
 * L2 handles:
 * - Collateral ratio calculations
 * - Debt tracking
 * - Interest calculations
 * - Position management
 * - Liquidation logic
 */
contract CollateralVaultL1 is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // ============ State Variables ============

    // Loyalty Points token address
    IERC20 public immutable collateralToken;

    // User collateral balances (locked on L1)
    mapping(address => uint256) public lockedCollateral;

    // Total collateral locked in vault
    uint256 public totalLocked;

    // L2 Bridge address (authorized to lock/unlock)
    address public l2Bridge;

    // L1 State Registry (for emergency operations)
    address public stateRegistry;

    // Emergency pause flag
    bool public emergencyPaused;

    // Emergency withdrawal tracking (7-day delay)
    struct EmergencyWithdrawal {
        uint256 amount;
        uint256 requestTime;
        bool executed;
    }

    mapping(address => EmergencyWithdrawal) public emergencyWithdrawals;

    uint256 public constant EMERGENCY_DELAY = 7 days;

    // Rate limiting for security
    uint256 public constant MAX_LOCK_PER_TX = 1_000_000 * 1e18; // 1M tokens
    uint256 public dailyLockLimit = 10_000_000 * 1e18; // 10M tokens per day
    uint256 public dailyLockedAmount;
    uint256 public lastLockResetTime;

    // ============ Events ============

    event CollateralLocked(
        address indexed user,
        uint256 amount,
        uint256 totalUserLocked,
        bytes32 indexed l2TxHash
    );

    event CollateralUnlocked(
        address indexed user,
        uint256 amount,
        uint256 remaining,
        bytes32 indexed l2TxHash
    );

    event EmergencyWithdrawalRequested(
        address indexed user,
        uint256 amount,
        uint256 unlockTime
    );

    event EmergencyWithdrawalExecuted(
        address indexed user,
        uint256 amount
    );

    event L2BridgeUpdated(address indexed oldBridge, address indexed newBridge);
    event StateRegistryUpdated(address indexed oldRegistry, address indexed newRegistry);
    event EmergencyPauseTriggered(address indexed triggeredBy);
    event EmergencyResumed(address indexed resumedBy);

    // ============ Modifiers ============

    modifier onlyBridge() {
        require(msg.sender == l2Bridge, "Only L2 bridge");
        _;
    }

    modifier whenNotPaused() {
        require(!emergencyPaused, "Emergency paused");
        _;
    }

    // ============ Constructor ============

    constructor(address _collateralToken) Ownable(msg.sender) {
        require(_collateralToken != address(0), "Invalid token address");
        collateralToken = IERC20(_collateralToken);
        lastLockResetTime = block.timestamp;
    }

    // ============ Core Functions ============

    /**
     * @notice Lock collateral from user (called by bridge)
     * @param user User address
     * @param amount Amount to lock
     * @param l2TxHash L2 transaction hash for tracking
     */
    function lockCollateral(address user, uint256 amount, bytes32 l2TxHash)
        external
        onlyBridge
        whenNotPaused
        nonReentrant
    {
        require(user != address(0), "Invalid user");
        require(amount > 0, "Amount must be > 0");
        require(amount <= MAX_LOCK_PER_TX, "Exceeds max per tx");

        // Check daily limit
        _checkAndUpdateDailyLimit(amount);

        // Transfer tokens from user to vault
        collateralToken.safeTransferFrom(user, address(this), amount);

        // Update balances
        lockedCollateral[user] += amount;
        totalLocked += amount;

        emit CollateralLocked(user, amount, lockedCollateral[user], l2TxHash);
    }

    /**
     * @notice Unlock collateral to user (called by bridge)
     * @param user User address
     * @param amount Amount to unlock
     * @param l2TxHash L2 transaction hash for tracking
     */
    function unlockCollateral(address user, uint256 amount, bytes32 l2TxHash)
        external
        onlyBridge
        whenNotPaused
        nonReentrant
    {
        require(user != address(0), "Invalid user");
        require(amount > 0, "Amount must be > 0");
        require(lockedCollateral[user] >= amount, "Insufficient locked collateral");

        // Update balances
        lockedCollateral[user] -= amount;
        totalLocked -= amount;

        // Transfer tokens back to user
        collateralToken.safeTransfer(user, amount);

        emit CollateralUnlocked(user, amount, lockedCollateral[user], l2TxHash);
    }

    /**
     * @notice Check and update daily lock limit
     * @dev Internal function to prevent over-locking
     */
    function _checkAndUpdateDailyLimit(uint256 amount) internal {
        // Reset daily counter if 24 hours passed
        if (block.timestamp >= lastLockResetTime + 1 days) {
            dailyLockedAmount = 0;
            lastLockResetTime = block.timestamp;
        }

        require(
            dailyLockedAmount + amount <= dailyLockLimit,
            "Exceeds daily lock limit"
        );

        dailyLockedAmount += amount;
    }

    // ============ Emergency Functions ============

    /**
     * @notice Request emergency withdrawal (7-day delay)
     * @param amount Amount to withdraw
     */
    function requestEmergencyWithdrawal(uint256 amount) external nonReentrant {
        require(emergencyPaused, "Not in emergency mode");
        require(lockedCollateral[msg.sender] >= amount, "Insufficient collateral");
        require(!emergencyWithdrawals[msg.sender].executed, "Already requested");
        require(emergencyWithdrawals[msg.sender].requestTime == 0, "Pending request exists");

        emergencyWithdrawals[msg.sender] = EmergencyWithdrawal({
            amount: amount,
            requestTime: block.timestamp,
            executed: false
        });

        emit EmergencyWithdrawalRequested(
            msg.sender,
            amount,
            block.timestamp + EMERGENCY_DELAY
        );
    }

    /**
     * @notice Execute emergency withdrawal after delay
     */
    function executeEmergencyWithdrawal() external nonReentrant {
        EmergencyWithdrawal storage withdrawal = emergencyWithdrawals[msg.sender];

        require(withdrawal.requestTime > 0, "No withdrawal request");
        require(!withdrawal.executed, "Already executed");
        require(
            block.timestamp >= withdrawal.requestTime + EMERGENCY_DELAY,
            "Delay period not passed"
        );
        require(lockedCollateral[msg.sender] >= withdrawal.amount, "Insufficient collateral");

        // Mark as executed
        withdrawal.executed = true;

        // Update balances
        lockedCollateral[msg.sender] -= withdrawal.amount;
        totalLocked -= withdrawal.amount;

        // Transfer tokens
        collateralToken.safeTransfer(msg.sender, withdrawal.amount);

        emit EmergencyWithdrawalExecuted(msg.sender, withdrawal.amount);
    }

    /**
     * @notice Trigger emergency pause
     */
    function triggerEmergencyPause() external onlyOwner {
        emergencyPaused = true;
        emit EmergencyPauseTriggered(msg.sender);
    }

    /**
     * @notice Resume from emergency
     */
    function resumeFromEmergency() external onlyOwner {
        emergencyPaused = false;
        emit EmergencyResumed(msg.sender);
    }

    // ============ Admin Functions ============

    /**
     * @notice Set L2 bridge address
     * @param _l2Bridge L2 bridge address
     */
    function setL2Bridge(address _l2Bridge) external onlyOwner {
        require(_l2Bridge != address(0), "Invalid bridge address");
        address oldBridge = l2Bridge;
        l2Bridge = _l2Bridge;
        emit L2BridgeUpdated(oldBridge, _l2Bridge);
    }

    /**
     * @notice Set state registry address
     * @param _stateRegistry State registry address
     */
    function setStateRegistry(address _stateRegistry) external onlyOwner {
        require(_stateRegistry != address(0), "Invalid registry address");
        address oldRegistry = stateRegistry;
        stateRegistry = _stateRegistry;
        emit StateRegistryUpdated(oldRegistry, _stateRegistry);
    }

    /**
     * @notice Update daily lock limit
     * @param newLimit New daily limit
     */
    function setDailyLockLimit(uint256 newLimit) external onlyOwner {
        dailyLockLimit = newLimit;
    }

    // ============ View Functions ============

    /**
     * @notice Get user's locked collateral
     * @param user User address
     */
    function getLockedCollateral(address user) external view returns (uint256) {
        return lockedCollateral[user];
    }

    /**
     * @notice Get total locked collateral
     */
    function getTotalLocked() external view returns (uint256) {
        return totalLocked;
    }

    /**
     * @notice Get vault statistics
     */
    function getVaultStats()
        external
        view
        returns (uint256 _totalLocked, uint256 contractBalance)
    {
        return (totalLocked, collateralToken.balanceOf(address(this)));
    }

    /**
     * @notice Get emergency withdrawal info
     * @param user User address
     */
    function getEmergencyWithdrawal(address user)
        external
        view
        returns (uint256 amount, uint256 requestTime, bool executed, uint256 unlockTime)
    {
        EmergencyWithdrawal memory withdrawal = emergencyWithdrawals[user];
        return (
            withdrawal.amount,
            withdrawal.requestTime,
            withdrawal.executed,
            withdrawal.requestTime > 0 ? withdrawal.requestTime + EMERGENCY_DELAY : 0
        );
    }

    /**
     * @notice Check if emergency withdrawal can be executed
     * @param user User address
     */
    function canExecuteEmergencyWithdrawal(address user) external view returns (bool) {
        EmergencyWithdrawal memory withdrawal = emergencyWithdrawals[user];
        return
            withdrawal.requestTime > 0 &&
            !withdrawal.executed &&
            block.timestamp >= withdrawal.requestTime + EMERGENCY_DELAY;
    }

    /**
     * @notice Get remaining daily lock capacity
     */
    function getRemainingDailyLockCapacity() external view returns (uint256) {
        // Check if we need to reset
        if (block.timestamp >= lastLockResetTime + 1 days) {
            return dailyLockLimit;
        }

        if (dailyLockedAmount >= dailyLockLimit) {
            return 0;
        }

        return dailyLockLimit - dailyLockedAmount;
    }

    /**
     * @notice Get time until daily limit resets
     */
    function getTimeUntilLimitReset() external view returns (uint256) {
        uint256 resetTime = lastLockResetTime + 1 days;
        if (block.timestamp >= resetTime) {
            return 0;
        }
        return resetTime - block.timestamp;
    }
}

contracts/layer1/core/L1StateRegistry.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title L1StateRegistry
 * @notice Layer 1 state registry for Layer 2 state commitments
 * @dev Stores merkle roots from L2 and enables verification
 *
 * Architecture:
 * - Receives state roots from L2 via Arbitrum bridge
 * - Validates state commitments
 * - Triggers critical L1 operations (liquidations, emergency exits)
 * - Maintains historical state for fraud proofs
 */
contract L1StateRegistry is Ownable, ReentrancyGuard {
    // ============ State Variables ============

    // Mapping: L2 block number => state root
    mapping(uint256 => bytes32) public stateRoots;

    // Mapping: L2 block number => timestamp
    mapping(uint256 => uint256) public stateTimestamps;

    // Latest L2 block number submitted
    uint256 public latestL2Block;

    // Minimum time between state submissions (prevents spam)
    uint256 public constant MIN_SUBMISSION_INTERVAL = 1 hours;

    // Last submission timestamp
    uint256 public lastSubmissionTime;

    // Authorized L2 address (Arbitrum L2 contract)
    address public l2StateAggregator;

    // Emergency pause flag
    bool public emergencyPaused;

    // Critical state thresholds
    struct SystemThresholds {
        uint256 minCollateralRatio;      // 150 = 150%
        uint256 liquidationThreshold;    // 120 = 120%
        uint256 maxDebtCeiling;          // Maximum total debt allowed
    }

    SystemThresholds public thresholds;

    // State snapshot structure
    struct StateSnapshot {
        bytes32 stateRoot;
        uint256 l2BlockNumber;
        uint256 timestamp;
        uint256 totalCollateral;
        uint256 totalDebt;
        bool criticalCondition;
    }

    // Historical snapshots
    StateSnapshot[] public snapshots;

    // Emergency exit tracking
    mapping(address => bool) public emergencyExitClaimed;
    mapping(address => uint256) public emergencyExitAmount;

    // ============ Events ============

    event StateRootReceived(
        bytes32 indexed stateRoot,
        uint256 indexed l2Block,
        uint256 timestamp
    );

    event CriticalConditionDetected(
        bytes32 stateRoot,
        uint256 totalCollateral,
        uint256 totalDebt,
        uint256 ratio
    );

    event EmergencyPauseTriggered(address indexed triggeredBy, string reason);

    event EmergencyExitInitiated(
        address indexed user,
        uint256 amount,
        uint256 blockNumber
    );

    event L2AggregatorUpdated(address indexed oldAggregator, address indexed newAggregator);

    event ThresholdsUpdated(
        uint256 minCollateralRatio,
        uint256 liquidationThreshold,
        uint256 maxDebtCeiling
    );

    // ============ Modifiers ============

    modifier onlyL2Aggregator() {
        require(msg.sender == l2StateAggregator, "L1Registry: Only L2 aggregator");
        _;
    }

    modifier whenNotPaused() {
        require(!emergencyPaused, "L1Registry: Emergency paused");
        _;
    }

    // ============ Constructor ============

    constructor(address _l2StateAggregator) Ownable(msg.sender) {
        require(_l2StateAggregator != address(0), "Invalid L2 aggregator");

        l2StateAggregator = _l2StateAggregator;

        // Set default thresholds
        thresholds = SystemThresholds({
            minCollateralRatio: 150,
            liquidationThreshold: 120,
            maxDebtCeiling: 10_000_000 * 1e6  // 10M LUSD
        });

        lastSubmissionTime = block.timestamp;
    }

    // ============ Core Functions ============

    /**
     * @notice Receive state root from L2
     * @param stateRoot Merkle root of L2 state
     * @param l2Block L2 block number
     * @param totalCollateral Total collateral in system
     * @param totalDebt Total debt in system
     */
    function receiveStateRoot(
        bytes32 stateRoot,
        uint256 l2Block,
        uint256 totalCollateral,
        uint256 totalDebt
    ) external onlyL2Aggregator whenNotPaused nonReentrant {
        require(stateRoot != bytes32(0), "Invalid state root");
        require(l2Block > latestL2Block, "L2 block not newer");
        require(
            block.timestamp >= lastSubmissionTime + MIN_SUBMISSION_INTERVAL,
            "Submission too frequent"
        );

        // Store state root
        stateRoots[l2Block] = stateRoot;
        stateTimestamps[l2Block] = block.timestamp;
        latestL2Block = l2Block;
        lastSubmissionTime = block.timestamp;

        // Check for critical conditions
        bool critical = _checkCriticalConditions(totalCollateral, totalDebt);

        // Create snapshot
        snapshots.push(StateSnapshot({
            stateRoot: stateRoot,
            l2BlockNumber: l2Block,
            timestamp: block.timestamp,
            totalCollateral: totalCollateral,
            totalDebt: totalDebt,
            criticalCondition: critical
        }));

        emit StateRootReceived(stateRoot, l2Block, block.timestamp);
    }

    /**
     * @notice Verify a state commitment exists
     * @param l2Block L2 block number
     * @param expectedRoot Expected state root
     * @return bool True if state root matches
     */
    function verifyStateRoot(uint256 l2Block, bytes32 expectedRoot)
        external
        view
        returns (bool)
    {
        return stateRoots[l2Block] == expectedRoot && expectedRoot != bytes32(0);
    }

    /**
     * @notice Get the latest state root
     * @return stateRoot Latest state root
     * @return l2Block Latest L2 block number
     * @return timestamp Timestamp of submission
     */
    function getLatestState()
        external
        view
        returns (bytes32 stateRoot, uint256 l2Block, uint256 timestamp)
    {
        return (
            stateRoots[latestL2Block],
            latestL2Block,
            stateTimestamps[latestL2Block]
        );
    }

    /**
     * @notice Get state root for specific L2 block
     * @param l2Block L2 block number
     */
    function getStateRoot(uint256 l2Block) external view returns (bytes32) {
        return stateRoots[l2Block];
    }

    /**
     * @notice Get snapshot by index
     * @param index Snapshot index
     */
    function getSnapshot(uint256 index)
        external
        view
        returns (StateSnapshot memory)
    {
        require(index < snapshots.length, "Invalid index");
        return snapshots[index];
    }

    /**
     * @notice Get total number of snapshots
     */
    function getSnapshotCount() external view returns (uint256) {
        return snapshots.length;
    }

    // ============ Critical Condition Management ============

    /**
     * @notice Check if system meets critical conditions
     * @dev Internal function called when receiving state
     */
    function _checkCriticalConditions(
        uint256 totalCollateral,
        uint256 totalDebt
    ) internal returns (bool) {
        if (totalDebt == 0) return false;

        // Calculate system collateral ratio
        uint256 systemRatio = (totalCollateral * 100) / totalDebt;

        bool critical = false;

        // Check if system is under-collateralized
        if (systemRatio < thresholds.minCollateralRatio) {
            critical = true;
            emit CriticalConditionDetected(
                stateRoots[latestL2Block],
                totalCollateral,
                totalDebt,
                systemRatio
            );
        }

        // Check if debt ceiling exceeded
        if (totalDebt > thresholds.maxDebtCeiling) {
            critical = true;
        }

        return critical;
    }

    // ============ Emergency Functions ============

    /**
     * @notice Trigger emergency pause
     * @param reason Reason for emergency pause
     */
    function triggerEmergencyPause(string calldata reason) external onlyOwner {
        emergencyPaused = true;
        emit EmergencyPauseTriggered(msg.sender, reason);
    }

    /**
     * @notice Resume from emergency pause
     */
    function resumeFromEmergency() external onlyOwner {
        emergencyPaused = false;
    }

    /**
     * @notice Initiate emergency exit for user
     * @param user User address
     * @param amount Amount to exit
     * @dev Only callable by owner in emergency situations
     */
    function initiateEmergencyExit(address user, uint256 amount)
        external
        onlyOwner
    {
        require(emergencyPaused, "Not in emergency");
        require(!emergencyExitClaimed[user], "Already claimed");

        emergencyExitAmount[user] = amount;

        emit EmergencyExitInitiated(user, amount, latestL2Block);
    }

    // ============ Admin Functions ============

    /**
     * @notice Update L2 aggregator address
     * @param newAggregator New aggregator address
     */
    function setL2Aggregator(address newAggregator) external onlyOwner {
        require(newAggregator != address(0), "Invalid address");
        address oldAggregator = l2StateAggregator;
        l2StateAggregator = newAggregator;
        emit L2AggregatorUpdated(oldAggregator, newAggregator);
    }

    /**
     * @notice Update system thresholds
     */
    function updateThresholds(
        uint256 _minCollateralRatio,
        uint256 _liquidationThreshold,
        uint256 _maxDebtCeiling
    ) external onlyOwner {
        require(_minCollateralRatio > _liquidationThreshold, "Invalid ratios");
        require(_liquidationThreshold > 100, "Threshold too low");

        thresholds.minCollateralRatio = _minCollateralRatio;
        thresholds.liquidationThreshold = _liquidationThreshold;
        thresholds.maxDebtCeiling = _maxDebtCeiling;

        emit ThresholdsUpdated(
            _minCollateralRatio,
            _liquidationThreshold,
            _maxDebtCeiling
        );
    }

    // ============ View Functions ============

    /**
     * @notice Check if state is fresh (submitted within last 2 hours)
     */
    function isStateFresh() external view returns (bool) {
        if (latestL2Block == 0) return false;
        return block.timestamp - stateTimestamps[latestL2Block] <= 2 hours;
    }

    /**
     * @notice Get time since last submission
     */
    function timeSinceLastSubmission() external view returns (uint256) {
        return block.timestamp - lastSubmissionTime;
    }

    /**
     * @notice Check if system can accept new submission
     */
    function canSubmit() external view returns (bool) {
        return block.timestamp >= lastSubmissionTime + MIN_SUBMISSION_INTERVAL;
    }
}

contracts/layer1/core/LoyaltyUSDL1.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title LoyaltyUSDL1
 * @notice Layer 1 optimized version - Only minting/burning authority
 * @dev Stripped down version for L1, business logic moved to L2
 *
 * L1 Responsibilities:
 * - Maintain minting/burning authority
 * - Final settlement of L2 state
 * - Emergency pause capability
 * - Cross-chain bridge integration
 *
 * L2 handles:
 * - All business logic
 * - Interest calculations
 * - Collateral ratio checks
 * - User interactions
 */
contract LoyaltyUSDL1 is ERC20, ERC20Burnable, AccessControl, Pausable {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant BRIDGE_ROLE = keccak256("BRIDGE_ROLE");

    // L2 Bridge address
    address public l2Bridge;

    // Minting limits for security
    uint256 public constant MAX_MINT_PER_TX = 1_000_000 * 1e6; // 1M LUSD
    uint256 public dailyMintLimit = 10_000_000 * 1e6; // 10M LUSD per day
    uint256 public dailyMintedAmount;
    uint256 public lastMintResetTime;

    // Events
    event Minted(address indexed to, uint256 amount, address indexed minter);
    event Burned(address indexed from, uint256 amount, address indexed burner);
    event EmergencyPaused(address indexed pauser);
    event EmergencyUnpaused(address indexed unpauser);
    event L2BridgeUpdated(address indexed oldBridge, address indexed newBridge);
    event DailyMintLimitUpdated(uint256 oldLimit, uint256 newLimit);
    event BridgeMint(address indexed to, uint256 amount, bytes32 indexed l2TxHash);
    event BridgeBurn(address indexed from, uint256 amount, bytes32 indexed l2TxHash);

    /**
     * @notice Constructor initializes L1 token with admin
     */
    constructor() ERC20("LoyaltyUSD", "LUSD") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(BURNER_ROLE, msg.sender);

        lastMintResetTime = block.timestamp;
    }

    /**
     * @notice Mint new LUSD tokens (L1 authority)
     * @param to Recipient address
     * @param amount Amount to mint (in 6 decimals)
     * @dev Only callable by MINTER_ROLE or BRIDGE_ROLE
     */
    function mint(address to, uint256 amount) external onlyRole(MINTER_ROLE) {
        require(to != address(0), "LUSD: mint to zero address");
        require(amount > 0, "LUSD: mint amount must be positive");
        require(amount <= MAX_MINT_PER_TX, "LUSD: exceeds max mint per tx");

        // Check daily limit
        _checkAndUpdateDailyLimit(amount);

        _mint(to, amount);
        emit Minted(to, amount, msg.sender);
    }

    /**
     * @notice Mint from L2 bridge (cross-chain)
     * @param to Recipient address
     * @param amount Amount to mint
     * @param l2TxHash L2 transaction hash for tracking
     */
    function bridgeMint(address to, uint256 amount, bytes32 l2TxHash)
        external
        onlyRole(BRIDGE_ROLE)
    {
        require(to != address(0), "LUSD: mint to zero address");
        require(amount > 0, "LUSD: amount must be positive");
        require(amount <= MAX_MINT_PER_TX, "LUSD: exceeds max mint per tx");

        // Check daily limit
        _checkAndUpdateDailyLimit(amount);

        _mint(to, amount);
        emit BridgeMint(to, amount, l2TxHash);
    }

    /**
     * @notice Burn LUSD tokens from an address
     * @param from Address to burn from
     * @param amount Amount to burn
     * @dev Only callable by BURNER_ROLE or token owner
     */
    function burn(address from, uint256 amount) public {
        require(
            msg.sender == from || hasRole(BURNER_ROLE, msg.sender),
            "LUSD: must have BURNER_ROLE or be token owner"
        );
        require(amount > 0, "LUSD: burn amount must be positive");

        _burn(from, amount);
        emit Burned(from, amount, msg.sender);
    }

    /**
     * @notice Burn from L2 bridge (cross-chain)
     * @param from Address to burn from
     * @param amount Amount to burn
     * @param l2TxHash L2 transaction hash for tracking
     */
    function bridgeBurn(address from, uint256 amount, bytes32 l2TxHash)
        external
        onlyRole(BRIDGE_ROLE)
    {
        require(amount > 0, "LUSD: amount must be positive");

        _burn(from, amount);
        emit BridgeBurn(from, amount, l2TxHash);
    }

    /**
     * @notice Check and update daily mint limit
     * @dev Internal function to prevent over-minting
     */
    function _checkAndUpdateDailyLimit(uint256 amount) internal {
        // Reset daily counter if 24 hours passed
        if (block.timestamp >= lastMintResetTime + 1 days) {
            dailyMintedAmount = 0;
            lastMintResetTime = block.timestamp;
        }

        require(
            dailyMintedAmount + amount <= dailyMintLimit,
            "LUSD: exceeds daily mint limit"
        );

        dailyMintedAmount += amount;
    }

    /**
     * @notice Returns 6 decimals to match USDC
     */
    function decimals() public pure override returns (uint8) {
        return 6;
    }

    /**
     * @notice Pause all token transfers
     * @dev Only callable by PAUSER_ROLE
     */
    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
        emit EmergencyPaused(msg.sender);
    }

    /**
     * @notice Unpause token transfers
     * @dev Only callable by PAUSER_ROLE
     */
    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
        emit EmergencyUnpaused(msg.sender);
    }

    /**
     * @notice Hook that is called before any transfer of tokens
     * @dev Overridden to add pausable functionality (OpenZeppelin 5.x uses _update)
     */
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        super._update(from, to, amount);
    }

    /**
     * @notice Set L2 bridge address
     * @param _l2Bridge L2 bridge address
     */
    function setL2Bridge(address _l2Bridge) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_l2Bridge != address(0), "Invalid bridge address");
        address oldBridge = l2Bridge;
        l2Bridge = _l2Bridge;

        // Grant bridge role
        _grantRole(BRIDGE_ROLE, _l2Bridge);

        // Revoke old bridge role if exists
        if (oldBridge != address(0)) {
            _revokeRole(BRIDGE_ROLE, oldBridge);
        }

        emit L2BridgeUpdated(oldBridge, _l2Bridge);
    }

    /**
     * @notice Update daily mint limit
     * @param newLimit New daily limit
     */
    function setDailyMintLimit(uint256 newLimit)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        uint256 oldLimit = dailyMintLimit;
        dailyMintLimit = newLimit;
        emit DailyMintLimitUpdated(oldLimit, newLimit);
    }

    /**
     * @notice Get total supply in human-readable format
     * @return Total supply with decimals applied
     */
    function totalSupplyFormatted() external view returns (uint256) {
        return totalSupply() / (10 ** decimals());
    }

    /**
     * @notice Check if address has minting capability
     */
    function canMint(address account) external view returns (bool) {
        return hasRole(MINTER_ROLE, account) || hasRole(BRIDGE_ROLE, account);
    }

    /**
     * @notice Check if address has burning capability
     */
    function canBurn(address account) external view returns (bool) {
        return hasRole(BURNER_ROLE, account) || hasRole(BRIDGE_ROLE, account);
    }

    /**
     * @notice Get remaining daily mint capacity
     */
    function getRemainingDailyMintCapacity() external view returns (uint256) {
        // Check if we need to reset
        if (block.timestamp >= lastMintResetTime + 1 days) {
            return dailyMintLimit;
        }

        if (dailyMintedAmount >= dailyMintLimit) {
            return 0;
        }

        return dailyMintLimit - dailyMintedAmount;
    }

    /**
     * @notice Get time until daily limit resets
     */
    function getTimeUntilLimitReset() external view returns (uint256) {
        uint256 resetTime = lastMintResetTime + 1 days;
        if (block.timestamp >= resetTime) {
            return 0;
        }
        return resetTime - block.timestamp;
    }
}

contracts/layer2/aggregator/L2StateAggre.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

/**
 * @title L2StateAggregator
 * @notice Layer 2 state aggregator for periodic L1 submissions
 * @dev Aggregates L2 state and submits merkle roots to L1
 *
 * Architecture:
 * - Collects state from all L2 modules
 * - Computes merkle root of system state
 * - Submits to L1 StateRegistry every hour
 * - Uses Arbitrum's native ArbSys for L1 messaging
 */

// Arbitrum ArbSys interface
interface IArbSys {
    function sendTxToL1(address destination, bytes calldata data)
        external
        payable
        returns (uint256);
}

contract L2StateAggregator is Ownable {
    // ============ Constants ============

    // Arbitrum ArbSys precompile address
    address constant ARBSYS_ADDR = address(100);

    // Minimum time between submissions (1 hour)
    uint256 public constant MIN_SUBMISSION_INTERVAL = 1 hours;

    // ============ State Variables ============

    // Current state root
    bytes32 public currentStateRoot;

    // Last submission timestamp
    uint256 public lastSubmission;

    // L1 State Registry address
    address public l1StateRegistry;

    // Module state tracking
    struct ModuleState {
        bytes32 stateHash;
        uint256 lastUpdate;
        bool active;
    }

    // Module registry
    mapping(bytes32 => ModuleState) public moduleStates;
    bytes32[] public moduleIds;

    // Aggregated system state
    struct SystemState {
        uint256 totalCollateral;
        uint256 totalDebt;
        uint256 activePositions;
        uint256 totalOrders;
        uint256 blockNumber;
        uint256 timestamp;
    }

    SystemState public systemState;

    // Historical state roots
    bytes32[] public stateHistory;
    mapping(bytes32 => uint256) public stateRootToBlock;

    // Authorized modules (can update state)
    mapping(address => bool) public authorizedModules;

    // ============ Events ============

    event StateRootComputed(
        bytes32 indexed stateRoot,
        uint256 blockNumber,
        uint256 timestamp
    );

    event StateSubmittedToL1(
        bytes32 indexed stateRoot,
        uint256 indexed l2Block,
        uint256 indexed l1TxId,
        uint256 totalCollateral,
        uint256 totalDebt
    );

    event ModuleStateUpdated(
        bytes32 indexed moduleId,
        bytes32 stateHash,
        address updatedBy
    );

    event ModuleRegistered(bytes32 indexed moduleId, address moduleAddress);
    event ModuleDeactivated(bytes32 indexed moduleId);
    event L1RegistryUpdated(address indexed oldRegistry, address indexed newRegistry);

    // ============ Modifiers ============

    modifier onlyAuthorizedModule() {
        require(authorizedModules[msg.sender], "Not authorized module");
        _;
    }

    modifier canSubmit() {
        require(
            block.timestamp >= lastSubmission + MIN_SUBMISSION_INTERVAL,
            "Submission too frequent"
        );
        _;
    }

    // ============ Constructor ============

    constructor(address _l1StateRegistry) Ownable(msg.sender) {
        require(_l1StateRegistry != address(0), "Invalid L1 registry");
        l1StateRegistry = _l1StateRegistry;
        lastSubmission = block.timestamp;
    }

    // ============ Core Functions ============

    /**
     * @notice Update module state
     * @param moduleId Module identifier
     * @param stateHash Hash of module state
     */
    function updateModuleState(bytes32 moduleId, bytes32 stateHash)
        external
        onlyAuthorizedModule
    {
        require(moduleId != bytes32(0), "Invalid module ID");
        require(stateHash != bytes32(0), "Invalid state hash");

        // Update or create module state
        if (!moduleStates[moduleId].active) {
            moduleIds.push(moduleId);
        }

        moduleStates[moduleId] = ModuleState({
            stateHash: stateHash,
            lastUpdate: block.timestamp,
            active: true
        });

        emit ModuleStateUpdated(moduleId, stateHash, msg.sender);
    }

    /**
     * @notice Update system-level state
     * @param totalCollateral Total collateral in system
     * @param totalDebt Total debt in system
     * @param activePositions Number of active positions
     * @param totalOrders Number of active orders
     */
    function updateSystemState(
        uint256 totalCollateral,
        uint256 totalDebt,
        uint256 activePositions,
        uint256 totalOrders
    ) external onlyAuthorizedModule {
        systemState = SystemState({
            totalCollateral: totalCollateral,
            totalDebt: totalDebt,
            activePositions: activePositions,
            totalOrders: totalOrders,
            blockNumber: block.number,
            timestamp: block.timestamp
        });
    }

    /**
     * @notice Calculate current state root
     * @return State root (merkle root of all module states)
     */
    function calculateStateRoot() public view returns (bytes32) {
        // Create array of module state hashes
        bytes32[] memory hashes = new bytes32[](moduleIds.length + 1);

        // Add module states
        for (uint256 i = 0; i < moduleIds.length; i++) {
            bytes32 moduleId = moduleIds[i];
            if (moduleStates[moduleId].active) {
                hashes[i] = keccak256(
                    abi.encodePacked(moduleId, moduleStates[moduleId].stateHash)
                );
            }
        }

        // Add system state
        hashes[moduleIds.length] = keccak256(
            abi.encodePacked(
                systemState.totalCollateral,
                systemState.totalDebt,
                systemState.activePositions,
                systemState.totalOrders,
                systemState.blockNumber,
                systemState.timestamp
            )
        );

        // Compute merkle root
        return _computeMerkleRoot(hashes);
    }

    /**
     * @notice Submit state to L1
     * @dev Can be called by anyone after MIN_SUBMISSION_INTERVAL
     */
    function submitToL1() external canSubmit {
        // Calculate state root
        bytes32 stateRoot = calculateStateRoot();
        currentStateRoot = stateRoot;

        // Record in history
        stateHistory.push(stateRoot);
        stateRootToBlock[stateRoot] = block.number;

        // Prepare L1 message
        bytes memory data = abi.encodeWithSignature(
            "receiveStateRoot(bytes32,uint256,uint256,uint256)",
            stateRoot,
            block.number,
            systemState.totalCollateral,
            systemState.totalDebt
        );

        // Send to L1 via ArbSys
        uint256 l1TxId = IArbSys(ARBSYS_ADDR).sendTxToL1(
            l1StateRegistry,
            data
        );

        // Update last submission time
        lastSubmission = block.timestamp;

        emit StateRootComputed(stateRoot, block.number, block.timestamp);
        emit StateSubmittedToL1(
            stateRoot,
            block.number,
            l1TxId,
            systemState.totalCollateral,
            systemState.totalDebt
        );
    }

    /**
     * @notice Force submit to L1 (owner only)
     * @dev Bypasses time restriction for emergencies
     */
    function forceSubmitToL1() external onlyOwner {
        bytes32 stateRoot = calculateStateRoot();
        currentStateRoot = stateRoot;

        stateHistory.push(stateRoot);
        stateRootToBlock[stateRoot] = block.number;

        bytes memory data = abi.encodeWithSignature(
            "receiveStateRoot(bytes32,uint256,uint256,uint256)",
            stateRoot,
            block.number,
            systemState.totalCollateral,
            systemState.totalDebt
        );

        uint256 l1TxId = IArbSys(ARBSYS_ADDR).sendTxToL1(
            l1StateRegistry,
            data
        );

        lastSubmission = block.timestamp;

        emit StateRootComputed(stateRoot, block.number, block.timestamp);
        emit StateSubmittedToL1(
            stateRoot,
            block.number,
            l1TxId,
            systemState.totalCollateral,
            systemState.totalDebt
        );
    }

    /**
     * @notice Compute merkle root from array of hashes
     * @dev Internal helper function
     */
    function _computeMerkleRoot(bytes32[] memory hashes)
        internal
        pure
        returns (bytes32)
    {
        uint256 n = hashes.length;
        if (n == 0) return bytes32(0);
        if (n == 1) return hashes[0];

        // Build merkle tree bottom-up
        while (n > 1) {
            uint256 j = 0;
            for (uint256 i = 0; i < n - 1; i += 2) {
                hashes[j] = keccak256(abi.encodePacked(hashes[i], hashes[i + 1]));
                j++;
            }
            if (n % 2 == 1) {
                hashes[j] = hashes[n - 1];
                j++;
            }
            n = j;
        }

        return hashes[0];
    }

    // ============ Module Management ============

    /**
     * @notice Register a new module
     * @param moduleId Module identifier
     * @param moduleAddress Module contract address
     */
    function registerModule(bytes32 moduleId, address moduleAddress)
        external
        onlyOwner
    {
        require(moduleId != bytes32(0), "Invalid module ID");
        require(moduleAddress != address(0), "Invalid module address");

        authorizedModules[moduleAddress] = true;

        emit ModuleRegistered(moduleId, moduleAddress);
    }

    /**
     * @notice Deactivate a module
     * @param moduleId Module identifier
     */
    function deactivateModule(bytes32 moduleId) external onlyOwner {
        require(moduleStates[moduleId].active, "Module not active");
        moduleStates[moduleId].active = false;
        emit ModuleDeactivated(moduleId);
    }

    /**
     * @notice Authorize a module address
     * @param moduleAddress Module contract address
     */
    function authorizeModule(address moduleAddress) external onlyOwner {
        require(moduleAddress != address(0), "Invalid address");
        authorizedModules[moduleAddress] = true;
    }

    /**
     * @notice Revoke module authorization
     * @param moduleAddress Module contract address
     */
    function revokeModuleAuthorization(address moduleAddress) external onlyOwner {
        authorizedModules[moduleAddress] = false;
    }

    // ============ Admin Functions ============

    /**
     * @notice Update L1 registry address
     * @param _l1StateRegistry New L1 registry address
     */
    function setL1StateRegistry(address _l1StateRegistry) external onlyOwner {
        require(_l1StateRegistry != address(0), "Invalid address");
        address oldRegistry = l1StateRegistry;
        l1StateRegistry = _l1StateRegistry;
        emit L1RegistryUpdated(oldRegistry, _l1StateRegistry);
    }

    // ============ View Functions ============

    /**
     * @notice Get module state
     * @param moduleId Module identifier
     */
    function getModuleState(bytes32 moduleId)
        external
        view
        returns (bytes32 stateHash, uint256 lastUpdate, bool active)
    {
        ModuleState memory state = moduleStates[moduleId];
        return (state.stateHash, state.lastUpdate, state.active);
    }

    /**
     * @notice Get number of registered modules
     */
    function getModuleCount() external view returns (uint256) {
        return moduleIds.length;
    }

    /**
     * @notice Get all module IDs
     */
    function getAllModuleIds() external view returns (bytes32[] memory) {
        return moduleIds;
    }

    /**
     * @notice Get state history length
     */
    function getStateHistoryLength() external view returns (uint256) {
        return stateHistory.length;
    }

    /**
     * @notice Get state root by index
     */
    function getStateRootByIndex(uint256 index) external view returns (bytes32) {
        require(index < stateHistory.length, "Invalid index");
        return stateHistory[index];
    }

    /**
     * @notice Check if can submit to L1
     */
    function canSubmitToL1() external view returns (bool) {
        return block.timestamp >= lastSubmission + MIN_SUBMISSION_INTERVAL;
    }

    /**
     * @notice Get time until next submission
     */
    function timeUntilNextSubmission() external view returns (uint256) {
        uint256 nextSubmissionTime = lastSubmission + MIN_SUBMISSION_INTERVAL;
        if (block.timestamp >= nextSubmissionTime) {
            return 0;
        }
        return nextSubmissionTime - block.timestamp;
    }

    /**
     * @notice Get current system state
     */
    function getSystemState()
        external
        view
        returns (
            uint256 totalCollateral,
            uint256 totalDebt,
            uint256 activePositions,
            uint256 totalOrders,
            uint256 blockNumber,
            uint256 timestamp
        )
    {
        return (
            systemState.totalCollateral,
            systemState.totalDebt,
            systemState.activePositions,
            systemState.totalOrders,
            systemState.blockNumber,
            systemState.timestamp
        );
    }
}

contracts/mocks/MockERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title MockERC20
 * @notice Mock ERC20 token for testing purposes
 */
contract MockERC20 is ERC20 {
    uint8 private _decimals;

    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply
    ) ERC20(name, symbol) {
        _decimals = 18;
        _mint(msg.sender, initialSupply);
    }

    /**
     * @notice Mint tokens to an address
     * @param to Recipient address
     * @param amount Amount to mint
     */
    function mint(address to, uint256 amount) external {
        _mint(to, amount);
    }

    /**
     * @notice Burn tokens from an address
     * @param from Address to burn from
     * @param amount Amount to burn
     */
    function burn(address from, uint256 amount) external {
        _burn(from, amount);
    }

    /**
     * @notice Set decimals for testing
     * @param decimals_ Number of decimals
     */
    function setDecimals(uint8 decimals_) external {
        _decimals = decimals_;
    }

    /**
     * @notice Get decimals
     */
    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }
}

